import React from 'react';

function InfoToolTip ({ name,
                        title,
                        isOpen,
                        onClose,
                        onSubmit,
                        onOverlayClick,
                        children }) {
  return (
    <div className={`popup popup_type_${name} ${isOpen ? 'popup_opened' : ''} popup_opened`}
         onMouseDown={onOverlayClick}
    >
      <div className={`popup__container popup__container_type_${name}`}>
        <button className="popup__close"
                id={`close-${name}`}
                type="button"
                onMouseDown={onClose}
        >
        </button>
        <div className="popup__message">
          <img className="popup__message-icon" src="../images/popup__tip-error.jpg" alt="Иконка" />
          <p className="popup__message-text">Что-то пошло не так! Попробуйте ещё раз.</p>
        </div>
      </div>
    </div>  
  )
}

export default InfoToolTip;
