import React                 from 'react';
import { Link, useNavigate } from 'react-router-dom';
import AuthForm              from './AuthForm';
import { auth }              from '../utils/auth';
import InfoToolTip           from "./InfoToolTip";

function Register() {

  const [isInfoToolTipOpen, setInfoToolTipOpen] = React.useState(false);
  const [infoToolTip,       setInfoToolTip    ] = React.useState({ message: '', positive: false });
  const [formValue,         setFormValue      ] = React.useState({ email: '', password: '' });
  const navigate = useNavigate();

  // Отправка формы

  function handleSubmit(evt) {
    evt.preventDefault();
    console.log(formValue);

    auth.register(formValue.email, formValue.password)
      .then(() => {
        setInfoToolTip({
          message: 'Вы успешно зарегистрировались!',
          positive: true
        });
        openInfoToolTip();
        // navigate('/sign-in', {replace: true});
      })
      .catch((error) =>{
        setInfoToolTip({
          message: 'Что-то пошло не так! Попробуйте ещё раз.',
          positive: false
        });
        setInfoToolTipOpen(true);
        console.log(error);
      })
  }

  // Открытие/закрытие модального окна с информацией об авторизации

  function openInfoToolTip() {
    setInfoToolTipOpen(true);
  }

  function closeInfoToolTip() {
    setInfoToolTipOpen(false);
    if (infoToolTip.positive) {
      navigate('/sign-in', {replace: true});
    }
  }

  function handlePopupOverlayClick(evt) {
    if (evt.target.classList.contains('popup_opened')) {
      closeInfoToolTip();
    }
  }

  // Обработчик изменений полей ввода

  function handleChange(evt) {
    setFormValue({
      ...formValue,
      [evt.target.name]: evt.target.value
    }) 
  }

  return (
    <AuthForm handleSubmit={handleSubmit}
              handleChange={handleChange}
              headingText={'Регистрация'}
              submitText={'Зарегистрироваться'}
              isInfoToolTipOpen={isInfoToolTipOpen}
              closeInfoToolTip={closeInfoToolTip}
              onOverlayClick={handlePopupOverlayClick}
              infoToolTip={infoToolTip}
    >
        <p className="auth__tip">
          Уже зарегистрированы? {<Link to="/sign-in" className="auth__link">
                                  Войти
                                </Link>}
        </p>
    </AuthForm>
  );
}

export default Register;
